# -*- coding: utf-8 -*-# @Time    : 2021/3/19 14:44# @Author  : LK# @File    : common.pyimport osimport timefrom datetime import datedef get_date_by_type(type):    return time.strftime(type,time.localtime(time.time()))def get_time():    return time.strftime("%H-%M-%S",time.localtime(time.time()))def get_date():    return  str(date.today())def save_screen_shot(driver):    sub_dir_path = create_dir_path("screenShot")    screen_name = os.path.join(sub_dir_path,(get_time()+"_screenShot.png"))    driver.get_screenshot_as_file(screen_name)def  get_allure_dir():    dir_report_path =create_dir_path("report")    allure_dir=os.path.join(dir_report_path,(get_time()+"_allure_report"))    return allure_dirdef get_report_path():    dir_report_path = create_dir_path("report")    report_name = os.path.join(dir_report_path, (get_time() + "_report.html"))    return report_namedef create_dir(path):    if not os.path.exists(path):        os.makedirs(path)def create_dir_path(dir):    root_dir_path =os.path.join(os.path.dirname(os.path.dirname(__file__)),dir)    sub_dir_path =os.path.join(root_dir_path,get_date())    create_dir(sub_dir_path)    return sub_dir_pathif __name__ == '__main__':    create_dir_path("report")